using UnityEngine.UI;using UnityEngine;namespace Original.UI {    // ボタンの基本処理を請け負う    public class ButtonProcess {                AudioSource audioSource_;   // 押したときのSE                public ButtonProcess(AudioSource audio = null) {                        audioSource_ = audio;        }        public void Push() {            if(audioSource_ != null) {                audioSource_.Play();            }        }    }}